<?phpclass Da_customer extends MY_Model {         public function __construct() {        parent::__construct();        $this->_table = "tc_customer";    }    public function inserts() {         $data = array(            'customer_name' => $this->customer_name,            'customer_tax_id_number' => $this->customer_tax_id_number,            'customer_address' => $this->customer_address,            'customer_telephone' => $this->customer_telephone,            'customer_type' => $this->customer_type,            'customer_status' => $this->customer_status,            'customer_create_by' => $this->customer_create_by,            'customer_create_date' => date('Y-m-d'),        );        return $this->insert($data);    }    public function inserts_array($data = null) {        return $this->insert($data);    }    public function updates_array($data = null, $key = null) {        return $this->update($key,$data);    }         public function deletes($id) {        $this->delete($id);    }    public function get_all_data($order =  "ASC") {        $this->order_by('id',$order);        return $this->get_all();    }        public function get_by_id($id) {        return $this->get($id);    }}