<?phpclass Da_formula extends MY_Model {    public function __construct() {        parent::__construct();        $this->_table = "tc_formula";    }    public function inserts() {        $data = array(            'FDA_thai' => $this->FDA_thai,            'FDA_english' => $this->FDA_english,            'product_type' => $this->product_type,            'product_appearance' => $this->product_appearance,            'formula_status' => 0,            'formula_create_by' => $this->formula_create_by,            'formula_create_date' => date('Y-m-d'),        );        return $this->insert($data);    }    public function inserts_array($data = null) {        return $this->insert($data);    }    public function updates_array($data = null, $key = null) {        return $this->update($key, $data);    }    public function deletes($id) {        $this->delete($id);    }    public function get_all_data() {        $this->order_by('id', "ASC");        return $this->get_all();    }    public function get_by_id($id) {        return $this->get($id);    }    public function get_data_limit($limit, $offset, $order = array(null, null), $search = null) {        $this->limit($limit, $offset);        if ($search != null and $search != "") {            $this->db->or_like('FDA_thai', $search);            $this->db->or_like('FDA_english', $search);            $this->db->or_like('formula_edit_by', $search);        }        if ($order[0] != null and $order[0] != "") {            switch ($order[0]) {                case 0:                    $this->db->order_by('id', $order[1]);                    break;                case 1:                    $this->db->order_by('FDA_thai', $order[1]);                    break;                case 2:                    $this->db->order_by('FDA_english', $order[1]);                    break;            }        }        return $this->get_all();    }    public function get_data_no_limit($order = array(null, null), $search = null) {        if ($search != null and $search != "") {            $this->db->or_like('FDA_thai', $search);            $this->db->or_like('FDA_english', $search);            $this->db->or_like('formula_edit_by', $search);        }        if ($order[0] != null and $order[0] != "") {            switch ($order[0]) {                case 0:                    $this->db->order_by('id', $order[1]);                    break;                case 1:                    $this->db->order_by('FDA_thai', $order[1]);                    break;                case 2:                    $this->db->order_by('FDA_english', $order[1]);                    break;            }        }        return $this->get_all();    }}