<div class="row">    <div class="col-md-12">        <div class="box box-primary">            <div class="box-header">            </div>            <div class="box-body">                <table class="table table-bordered table-autosort-serverside">                    <thead>                        <tr>                            <th style="width: 50px;">No.</th>                            <th style="width: 150px;">Materials Code</th>                            <th>trade_name</th>                            <th>trade_name 2</th>                            <th>Supplier</th>                            <th>Edit By</th>                             <th style="width: 100px;">Action.</th>                        </tr>                    </thead>                    <tbody></tbody>                </table>            </div>        </div>    </div></div><script type="text/javascript">    $(".table-autosort-serverside").DataTable({        "pageLength": 20,        "lengthMenu": [10, 20, 50, 100],        "processing": true,        "serverSide": true,        ajax: {            url: '<?php echo site_url('admin/materials/server_side'); ?>',            type: 'GET'        }    });    function material_edit(obj) {        var id = $(obj).data('id');        location.href = "./materials/edit/" + id;    }    function material_trash(obj) {        var id = $(obj).data('id');        var chk = confirm('คุณต้องการลบข้อมูลหรือไม่?');        if (chk) {            $.ajax({                url: "<?php echo site_url('admin/materials/remove'); ?>/" + id,                type: 'GET',                beforeSend: function (xhr) {                },                success: function (data, textStatus, jqXHR) {                },                error: function (jqXHR, textStatus, errorThrown) {                },                complete: function (jqXHR, textStatus) {                    location.reload();                }            });        }    }    function material_show(obj) {        var id = $(obj).data('id');        var status = $(obj).data('status');        var new_status = (status === 0) ? 1 : 0;        $(obj).data('status', new_status);        if (status === 0) {            $(obj).removeClass('fa-eye-slash');            $(obj).addClass('fa-eye');        } else {            $(obj).removeClass('fa-eye');            $(obj).addClass('fa-eye-slash');        }        $.ajax({            url: "<?php echo site_url('admin/materials/onshow'); ?>/" + id + "/" + new_status,            type: 'GET',            beforeSend: function (xhr) {            },            success: function (data, textStatus, jqXHR) {            },            error: function (jqXHR, textStatus, errorThrown) {            },            complete: function (jqXHR, textStatus) {            }        });    }</script>