      complete: function (jqXHR, textStatus) {                    location.reload();                }            });        }    }    function material_show(obj) {        var id = $(obj).data('id');        var status = $(obj).data('status');        var new_status = (status === 0) ? 1 : 0;        $(obj).data('status', new_status);        if (status === 0) {            $(obj).removeClass('fa-eye-slash');            $(obj).addClass('fa-eye');        } else {            $(obj).removeClass('fa-eye');            $(obj).addClass('fa-eye-slash');        }        $.ajax({            url: "<?php echo site_url('fdadocuments/materials/onshow'); ?>/" + id + "/" + new_status,            type: 'GET',            beforeSend: function (xhr) {            },            success: function (data, textStatus, jqXHR) {            },            error: function (jqXHR, textStatus, errorThrown) {            },            complete: function (jqXHR, textStatus) {            }        });    }</script>